import React, { useState } from "react"; // üí° Import useState
import { useLocation, useNavigate } from "react-router-dom";
import { useCart } from "./Contex/CartContext"; // üí° Import useCart hook
import "./ProductDetails.css";

const ProductDetails = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { addToCart } = useCart(); // üí° Get the addToCart function

  // Local state for quantity and size selection
  const [selectedQuantity, setSelectedQuantity] = useState(1); // üí° State for quantity
  const [selectedSize, setSelectedSize] = useState(null); // üí° State for size

  if (!state) {
    return (
      <div className="no-product">
        <h2>No Product Found</h2>
        <button onClick={() => navigate("/")}>Back to Shop</button>
      </div>
    );
  }

  const { id, image, title, description, price, category } = state; // üí° Ensure product ID is destructured

  // ... (Your size logic remains the same)
  let sizes = [];
  switch (category.toLowerCase()) {
    case "men's clothing":
    case "women's clothing":
      sizes = ["S", "M", "L", "XL"];
      break;
    case "jewelery":
      sizes = ["6", "7", "8", "9"];
      break;
    case "electronics":
      sizes = ["32in", "42in", "55in", "65in"];
      break;
    default:
      sizes = ["One Size"];
      break;
  }
  
  // Set initial selectedSize to the first available size if it hasn't been set
  React.useEffect(() => {
    if (!selectedSize && sizes.length > 0) {
      setSelectedSize(sizes[0]);
    }
  }, [sizes, selectedSize]);

  // Handler for adding the product to the cart
  const handleAddToCart = () => {
    if (!selectedSize) {
        alert("Please select a size before adding to cart.");
        return;
    }

    const productToAdd = { id, image, title, price }; // Only pass necessary info
    addToCart(productToAdd, selectedQuantity, selectedSize);
    alert(`${selectedQuantity} x ${title} added to cart!`); // Simple confirmation
  };

  return (
    <div className="product-details-container">
      <div className="product-image-section">
        <img src={image} alt={title} className="detail-img" />
      </div>

      <div className="product-info-section">
        <h4 className="category-text">{category.toUpperCase()}</h4>
        <h1 className="product-title">{title}</h1>
        <p className="product-description">{description}</p>

        <div className="price-section">
          <span className="price">${price}</span>
          <span className="old-price">${(price * 1.5).toFixed(2)}</span>
          <span className="discount-badge">-50%</span>
        </div>

        <div className="options">
          <label>Size:</label>
          <select 
            value={selectedSize}
            onChange={(e) => setSelectedSize(e.target.value)}
          >
            {sizes.map((size) => (
              <option key={size} value={size}>{size}</option>
            ))}
          </select>

          <label>Qty:</label>
          <input
            type="number"
            min="1"
            value={selectedQuantity}
            onChange={(e) => setSelectedQuantity(parseInt(e.target.value))}
          />
        </div>

        <div className="buttons">
          <button className="add-cart" onClick={handleAddToCart}>
            ADD TO CART
          </button>
          <button className="back-btn" onClick={() => navigate("/")}>
            ‚Üê BACK
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
