import React, { useContext } from "react";
import { CartContext } from "../Context/CartContext";
import "./CartD.css"; // make sure this path is correct

const Cart = () => {
  const { cartItems, removeFromCart, updateQuantity, cartTotal } = useContext(CartContext);

  if (!cartItems || cartItems.length === 0) {
    return <div className="cart-empty">Your cart is empty ðŸ›’</div>;
  }

  return (
    <div className="cart-details">
      <div className="cart-items">
        {cartItems.map((item) => (
          <div className="cart-row" key={item.id}>
            <img className="cart-thumb" src={item.image} alt={item.name} />
            <div className="cart-meta">
              <h4 className="cart-title">{item.name}</h4>
              <p className="cart-price">${item.price}</p>
              <div className="qty-control">
                <button
                  className="qty-btn"
                  onClick={() => updateQuantity(item.id, item.quantity - 1)}
                >
                  âˆ’
                </button>
                <input
                  type="number"
                  className="qty-input"
                  value={item.quantity}
                  min="1"
                  onChange={(e) => updateQuantity(item.id, Number(e.target.value))}
                />
                <button
                  className="qty-btn"
                  onClick={() => updateQuantity(item.id, item.quantity + 1)}
                >
                  +
                </button>
              </div>
            </div>

            <div className="cart-row-right">
              <p className="row-subtotal">
                ${(item.price * item.quantity).toFixed(2)}
              </p>
              <button className="remove-btn" onClick={() => removeFromCart(item.id)}>
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="cart-summary">
        <h3>Order Summary</h3>
        <p>Total: <strong>${cartTotal.toFixed(2)}</strong></p>
        <button className="checkout-btn">Checkout</button>
      </div>
    </div>
  );
};

export default Cart;
