import React, { useEffect, useState } from "react";
import "./Hero.css";
import { useNavigate } from "react-router-dom";

const Hero = () => {
  const [apiProducts, setApiProducts] = useState([]);
  const [customProducts, setCustomProducts] = useState([]);
  const navigate = useNavigate();

  // Load data
  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((data) => setApiProducts(data))
      .catch((err) => console.error("Error fetching data:", err));

    const storedProducts = JSON.parse(localStorage.getItem("customProducts")) || [];
    setCustomProducts(storedProducts);
  }, []);

  // Go to add product page
  const handleAddProduct = () => {
    navigate("/add-product");
  };

  // Buy Now button
  const handleBuyNow = (product) => {
    navigate(`/product/${product.id}`, { state: product });
  };

  // Delete custom product
  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this product?")) {
      const updatedProducts = customProducts.filter((p) => p.id !== id);
      setCustomProducts(updatedProducts);
      localStorage.setItem("customProducts", JSON.stringify(updatedProducts));
    }
  };

  // Combine both product lists
  const allProducts = [...apiProducts, ...customProducts];

  return (
    <div className="hero">
      <div className="hero-header">
        <h1>Shop</h1>
        <p>All You Need</p>
        <button className="add-product-btn" onClick={handleAddProduct}>
          + Add Product
        </button>
      </div>

      <div className="product-grid">
        {allProducts.length > 0 ? (
          allProducts.map((item) => (
            <div className="product-card" key={item.id}>
              <img src={item.image} alt={item.title} className="product-img" />
              <div className="product-info">
                <h3 className="product-title">
                  {item.title.length > 20 ? item.title.slice(0, 20) + "..." : item.title}
                </h3>
                <p className="product-category">{item.category}</p>
                <p className="product-price">${item.price}</p>
                <div className="product-buttons">
                  <button className="add-btn">Add to Cart</button>
                  <button className="buy-btn" onClick={() => handleBuyNow(item)}>
                    Buy Now
                  </button>

                  {/* Delete only for custom products */}
                  {customProducts.some((p) => p.id === item.id) && (
                    <button className="delete-btn" onClick={() => handleDelete(item.id)}>
                      Delete
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>Loading products...</p>
        )}
      </div>
    </div>
  );
};

export default Hero;
