import React, { useEffect, useState } from "react";
import "./Hero.css";
import { useNavigate } from "react-router-dom";

const Hero = () => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  // Fetch API + local products
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch("https://fakestoreapi.com/products");
        const apiData = await res.json();

        const localData = JSON.parse(localStorage.getItem("customProducts")) || [];
        setProducts([...apiData, ...localData]);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };

    fetchProducts();
  }, []);

  // Handle delete for custom-added products
  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this product?")) {
      const stored = JSON.parse(localStorage.getItem("customProducts")) || [];
      const updated = stored.filter((p) => p.id !== id);
      localStorage.setItem("customProducts", JSON.stringify(updated));

      setProducts((prev) => prev.filter((p) => p.id !== id));
    }
  };

  const handleBuyNow = (product) => {
    navigate(`/product/${product.id}`, { state: product });
  };

  const handleAddProduct = () => {
    navigate("/add-product");
  };

  return (
    <div className="hero">
      <div className="hero-header">
        <h1>Shop</h1>
        <p>All You Need</p>
        <button className="add-product-btn" onClick={handleAddProduct}>
          + Add Product
        </button>
      </div>

      <div className="product-grid">
        {products.map((item) => (
          <div className="product-card" key={item.id}>
            <img src={item.image} alt={item.title} className="product-img" />
            <div className="product-info">
              <h3 className="product-title">{item.title.slice(0, 20)}...</h3>
              <p className="product-category">{item.category}</p>
              <p className="product-price">${item.price}</p>
              <div className="product-buttons">
                <button className="add-btn">Add to Cart</button>
                <button className="buy-btn" onClick={() => handleBuyNow(item)}>
                  Buy Now
                </button>
                {/* Show delete button only for custom-added products */}
                {item.id > 1000 && (
                  <button
                    className="delete-btn"
                    onClick={() => handleDelete(item.id)}
                  >
                    Delete
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Hero;
