import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useCart } from "../Context/CartContext.j"; // ✅ import context
import "./ProductDetails.css";

const ProductDetails = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { addToCart } = useCart(); // ✅ use addToCart

  if (!state) {
    return (
      <div className="no-product">
        <h2>No Product Found</h2>
        <button onClick={() => navigate("/")}>Back to Shop</button>
      </div>
    );
  }

  const { image, title, description, price, category } = state;

  // size logic
  let sizes = [];
  switch (category.toLowerCase()) {
    case "men's clothing":
    case "women's clothing":
      sizes = ["S", "M", "L", "XL"];
      break;
    case "jewelery":
      sizes = ["6", "7", "8", "9"];
      break;
    case "electronics":
      sizes = ["32in", "42in", "55in", "65in"];
      break;
    default:
      sizes = ["One Size"];
      break;
  }

  return (
    <div className="product-details-container">
      <div className="product-image-section">
        <img src={image} alt={title} className="detail-img" />
      </div>

      <div className="product-info-section">
        <h4 className="category-text">{category.toUpperCase()}</h4>
        <h1 className="product-title">{title}</h1>
        <p className="product-description">{description}</p>

        <div className="price-section">
          <span className="price">${price}</span>
          <span className="old-price">${(price * 1.5).toFixed(2)}</span>
          <span className="discount-badge">-50%</span>
        </div>

        <div className="options">
          <label>Size:</label>
          <select>
            {sizes.map((size) => (
              <option key={size}>{size}</option>
            ))}
          </select>

          <label>Qty:</label>
          <input type="number" min="1" defaultValue="1" />
        </div>

        <div className="buttons">
          <button
            className="add-cart"
            onClick={() => {
              addToCart(state);
              alert("✅ Added to Cart!");
            }}
          >
            ADD TO CART
          </button>
          <button className="back-btn" onClick={() => navigate("/")}>
            ← BACK
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
